extension.receive:
if (index == 0) {
	runtimeValues.inputIndex = 0;
	} else if (index == 1) {
		runtimeValues.inputIndex = 1;
	}

return {
		runtimeValues : runtimeValues
};

extension.execute:
var shindou = Number(messageValues.intensity); 

var payload = {
	player: properties.Player,
	value: shindou,               
	occurred_at: new Date().toISOString(), 
};

ajax ({
    url : properties.URL,
    data : JSON.stringify(payload),
    type : "post",
    timeout : 5000,
    success : function ( contents ) {
       log("Success");

        callbackSuccess( {
            resultType : "continue",
            runtimeValues : runtimeValues
        } );
    },
    error : function ( request, errorMessage ) {
        log("Weather : Network error");

        callbackSuccess( {
            resultType : "continue",
            runtimeValues : runtimeValues
        } );
    }
});

return {
    resultType : "pause"
};
